// n8n Function Node: Process supplier data using nested input data
// It assumes the input JSON has a structure like { data: [ { ... }, { ... }, ... ] }

// Extract the input data array
const inputData = $input.first().json.data; 

// Extract topic from the first item (or any item since they should all have the same topic_id)
const topic = inputData.length > 0 ? (inputData[0].topic_id || "") : "";

const supplierData = {};

for (const item of inputData) {
  // 1. Grab the raw string, e.g. "( Loopio, OtherCo )"
  const identifiedRaw = item["Identified_Suppliers"] || "";
  // 2. Strip parentheses
  const identifiedClean = identifiedRaw
    .replace(/^\s*\(\s*/, "")
    .replace(/\s*\)\s*$/, "");

  // 3. Split, trim, lowercase, filter
  const suppliers = identifiedClean
    .split(",")
    .map(s => s.trim().toLowerCase())
    .filter(s => s !== "");

  const link = item["Link"] || "";
  const relevanceRating = Number(item["Relevance_Rating"]) || 0;

  for (const supplier of suppliers) {
    if (!supplierData[supplier]) {
      supplierData[supplier] = {
        links: new Set(),
        total_relevance: 0,
        topic: topic
      };
    }
    if (link) {
      supplierData[supplier].links.add(link);
    }
    supplierData[supplier].total_relevance += relevanceRating;
  }
}

// Build and sort output
const outputData = Object.entries(supplierData).map(([supplier, info]) => ({
  "Identified Suppliers": supplier,
  "Mentioned Links": Array.from(info.links).join("; "),
  "Final Score": info.total_relevance,
  "Topic": info.topic
})).sort((a, b) => b["Final Score"] - a["Final Score"]);

// Return for n8n
return outputData.map(data => ({ json: data }));

